-- These are miscellaneous settings for better solo play

-- [OPTIONAL] Just sets the message of the day

UPDATE variables SET value='Welcome to the World of Everquest' WHERE varname='MOTD';

-- [OPTIONAL] This cuts off the players from running the special commands

UPDATE command_settings SET access=10 WHERE access<10;

-- These settings allow for better solo play

UPDATE rule_values SET rule_value='250' WHERE rule_name='Skills:MaxTrainTradeskills';
UPDATE rule_values SET rule_value='10' WHERE rule_name='Character:DeathItemLossLevel';
UPDATE rule_values SET rule_value='100' WHERE rule_name='Character:DeathExpLossLevel';
