UPDATE rule_values SET rule_value='300' WHERE rule_name='Character:ManaRegenMultiplier';
UPDATE rule_values SET rule_value='10' WHERE rule_name='Character:RestRegenTimeToActivate';
UPDATE rule_values SET rule_value='85' WHERE rule_name='Character:MaxLevel';
UPDATE rule_values SET rule_value = 'true' WHERE rule_name = 'Character:HealOnLevel';
UPDATE rule_values SET rule_value = '1' WHERE rule_name = 'Adventure:MinNumberForGroup';
UPDATE rule_values SET rule_value = '60' WHERE rule_name = 'Character:AutosaveIntervals';
UPDATE zone SET fast_regen_hp=30, fast_regen_mana=30, fast_regen_endurance=30;

-- This removes the no dropping or transferring flags on items

UPDATE items SET nodrop=1;
UPDATE items SET notransfer=0;

-- [OPTIONAL] This removes the zone requirements to allow you to go wherever you want

UPDATE zone SET min_status=0, min_level=0, flag_needed='';

-- This adds more coins to creatures

UPDATE loottable SET mincash = (mincash * 2), maxcash = (maxcash * 4);

-- [OPTIONAL] This makes mounts more affordable

UPDATE items SET price=(price / 100), nodrop=1, notransfer=0 WHERE name LIKE '%Bridle' AND price>7999999;
UPDATE items SET price=(price / 100), nodrop=1, notransfer=0 WHERE name LIKE '%Drum' AND price>8999999;

-- This changes all of the Priests of Discord to Teachers of Norrath
-- Since PVP isn't necessary, these teachers allow you to spend your 
-- skill points better

UPDATE npc_types SET name='Teacher_of_Norrath', lastname='Scholar of the Amethyst Blade', class='11', race='5', texture='15', d_melee_texture1='8', loottable_id='0', face='2' WHERE name='Priest_of_Discord' OR name='Teacher_of_Norrath';

